import React from 'react';
import GoogleMapReact from 'google-map-react';

import './styles.css'
import './marker.css'

const DistanceResult = ({ distance, duration }) => {
  if (distance) {
    return (
        <ul className="distance-item">
        <header>
          <div className="distance-info">
            <strong>
              <p>The distance is {distance} and the journey takes {duration}. </p>
            </strong>
          </div>
        </header>
      </ul>
    )
} else {
  return(
    <h1> </h1>
  )}
};

function zoomDefined(distance) {
  let distanceInt = distance.replace(',', '')
  distanceInt = parseInt(distanceInt, 10)
 
  if (distanceInt <= 10) return 13
  if (distanceInt > 10 && distanceInt <= 20) return 12
  if (distanceInt > 20 && distanceInt <= 30) return 11
  if (distanceInt > 30 && distanceInt <= 60) return 10
  if (distanceInt > 60 && distanceInt <= 200) return 9
  if (distanceInt > 200 && distanceInt <= 400) return 8
  if (distanceInt > 400 && distanceInt <= 800) return 7
  if (distanceInt > 800 && distanceInt <= 2000) return 6
  if (distanceInt > 2000 && distanceInt <= 4000) return 5
  if (distanceInt > 4000 && distanceInt <= 7500) return 4
  if (distanceInt > 7500) return 3
}

function centerDefined(origins, destinations) {
  let center = { lat: (origins.lat + destinations.lat) / 2, lng: (origins.lng + destinations.lng) / 2 }
  return center
}

const Map = ({ distance, origins, destinations }) => {
  
  const center = centerDefined(origins, destinations)
  const zoom = zoomDefined(distance)

  if ((zoom && center) && origins && destinations) {
    return(
      <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={ { key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' } }
        defaultCenter={center}
        defaultZoom={zoom}
      >
        <Marker lat={origins.lat} lng={origins.lng} text="A" />
        <Marker lat={destinations.lat} lng={destinations.lng} text="B" />
      </GoogleMapReact>
    </div>
    )
  } else {
    return(
      <h1> </h1>
  )}
}

const Marker = ({text}) => {
  return <>
    <div className="pin"></div>
    <div className="pulse"></div>
      <div className="text">{text}</div>
  </>
}

export {Map, DistanceResult};
