import React, { Component, useState, useEffect } from 'react';
import GoogleMapReact, { Marker } from 'google-map-react';

import './styles.css';

/*
function DistanceResult({ distance, duration }) {
  if (distance) {
    return (
        <ul className="distance-item">
        <header>
          <div className="distance-info">
            <strong>
              <p>The distance is {distance} and the journey takes {duration}. </p>
            </strong>
          </div>
        </header>
      </ul>
    )
} else {
  return(
    <h1></h1>
  )}
};

export default DistanceResult;
*/

function Maps() {

  const [latitude, setLatitude] = useState(0)
  const [longitude, setLongitude] = useState(0)
  
  useEffect(() => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords
  
          setLatitude(latitude)
          setLongitude(longitude)
        },
        (err) => {
          console.log(err)
        },
        {
          timeout: 30000,
        }
      )
    }, [])

const text = 'Maps'
const AnyReactComponent = ({ text }) => <div>{text}</div>;

const zoom = 15
const center = {
      lat: -23.17,
      lng: -45.85
}

console.log(`Latitude: ${latitude} Longitude: ${longitude}`)

  return(
    <div style={{ height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' }}
      defaultCenter={center}
      defaultZoom={zoom}
    >
      <AnyReactComponent
        lat={-23.1735384}
        lng={-45.8531392,17}
        text="My Marker"
      />
      <Marker position={{ lat: -23.17, lng: -45.85}} />    
    </GoogleMapReact>
  </div>
  );
  }

export default Maps;
