import React from 'react';
import GoogleMapReact from 'google-map-react';

import './styles.css'
import './marker.css'

const DistanceResult = ({ distance, duration }) => {
  if (distance) {
    return (
        <ul className="distance-item">
        <header>
          <div className="distance-info">
            <strong>
              <p>The distance is {distance} and the journey takes {duration}. </p>
            </strong>
          </div>
        </header>
      </ul>
    )
} else {
  return(
    <h1> </h1>
  )}
};

function zoomDefined(distance) {
  distance = distance.parseInt(distance)
  console.log(distance)
  if (distance <= '40 km') return 11
  if (distance > '40 km' && distance <= '60 km') return 9
  if (distance > '60 km' && distance <= '200 km') return 8
  if (distance > '200 km' && distance <= '400 km') return 7
  if (distance > '400 km') return 6
}

const Map = ({ distance, origins, destinations }) => {
  
  const zoom = zoomDefined(distance)
  console.log(zoom)
  console.log(distance)

  if ((zoom) && origins||destinations) {
    return(
      <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={ { key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' } }
        defaultCenter={destinations}
        defaultZoom={zoom}
      >
        <Marker lat={origins.lat} lng={origins.lng} text="A" />
        <Marker lat={destinations.lat} lng={destinations.lng} text="B" />
      </GoogleMapReact>
    </div>
    )
  } else {
    return(
      <h1> </h1>
  )}
}

const Marker = ({text}) => {
  return <>
    <div className="pin"></div>
    <div className="pulse"></div>
      <div className="text">{text}</div>
  </>
}

export {Map, DistanceResult};
