import React, { Component, useState, useEffect } from 'react';
import GoogleMapReact from 'google-map-react';

import './styles.css';

/*
function DistanceResult({ distance, duration }) {
  if (distance) {
    return (
        <ul className="distance-item">
        <header>
          <div className="distance-info">
            <strong>
              <p>The distance is {distance} and the journey takes {duration}. </p>
            </strong>
          </div>
        </header>
      </ul>
    )
} else {
  return(
    <h1></h1>
  )}
};

export default DistanceResult;
*/

async function Maps() {

  const [latitude, setLatitude] = useState(0)
  const [longitude, setLongitude] = useState(0)
  
  useEffect(async () => {
    try{
      const position = navigator.geolocation.getCurrentPosition()
          const { latitude, longitude } = position.coords
  
          setLatitude(latitude)
          setLongitude(longitude)

          return position;
    } catch (error) {
      console.log(error)
    }
  }, [])

const text = 'Maps'
const AnyReactComponent = ({ text }) => <div>{text}</div>;

const zoom = 11
const center = {
      lat: latitude,
      lng: longitude
}

console.log(`Latitude: ${latitude} Longitude: ${longitude}`)

  return(
    <div style={{ height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' }}
      defaultCenter={center}
      defaultZoom={zoom}
    >
      <AnyReactComponent
        lat={latitude}
        lng={longitude}
        text="My Marker"
      />
    </GoogleMapReact>
  </div>
  );
  }

export default Maps;
