import React, { useState, useEffect } from 'react';
import GoogleMapReact from 'google-map-react';

import './styles.css'
import './marker.css'

/*
function DistanceResult({ distance, duration }) {
  if (distance) {
    return (
        <ul className="distance-item">
        <header>
          <div className="distance-info">
            <strong>
              <p>The distance is {distance} and the journey takes {duration}. </p>
            </strong>
          </div>
        </header>
      </ul>
    )
} else {
  return(
    <h1></h1>
  )}
};

export default DistanceResult;
*/
/*
function Maps() {

  const [latitude, setLatitude] = useState(0)
  const [longitude, setLongitude] = useState(0)
  
  useEffect(() => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords
  
          setLatitude(latitude)
          setLongitude(longitude)
        },
        (err) => {
          console.log(err)
        },
        {
          timeout: 30000,
        }
      )
    }, [])
*/

let text = ''
const zoom = 16
const center = {
      lat: -23.1720191,
      lng: -45.8469627
}

//console.log(`Latitude: ${latitude} Longitude: ${longitude}`)
const DistanceResult = () => {
  return(
    <div style={{ height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' }}
      defaultCenter={center}
      defaultZoom={zoom}
    >
      <Marker lat={center.lat} lng={center.lng} text="A" />
      <Marker lat={-23.1713906} lng={-45.8385853} text="B" />
    </GoogleMapReact>
  </div>
  )
}

const Marker = ({text}) => {
  return <>
    <div className="pin"></div>
    <div className="pulse"></div>
      <div className="pin text">{text}</div>
  </>
}

export default DistanceResult;
