import React, { Component, useState, useEffect } from 'react';
import GoogleMapReact from 'google-map-react';

import './styles.css';

/*
function DistanceResult({ distance, duration }) {
  if (distance) {
    return (
        <ul className="distance-item">
        <header>
          <div className="distance-info">
            <strong>
              <p>The distance is {distance} and the journey takes {duration}. </p>
            </strong>
          </div>
        </header>
      </ul>
    )
} else {
  return(
    <h1></h1>
  )}
};

export default DistanceResult;
*/
let getLatLng = {latitude: 0, longitude: 0}

function Maps() {

  navigator.geolocation.getCurrentPosition(
    (position) => {
      const { lat, lng } = position.coords
      let getLatLng = {
        latidude: lat,
        longitude: lng
      }
      console.log(lat, lng)
      return getLatLng;
    },
    (err) => {
      console.log(err)
    },
    {
      timeout: 30000,
    }
  )

const text = 'Maps'
const AnyReactComponent = ({ text }) => <div>{text}</div>;

class SimpleMap extends Component {
  static defaultProps = {
    center: {
      lat: 59.95,
      lng: 30.33
    },
    zoom: 11
  };

render() {
  return(
    <div style={{ height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' }}
      defaultCenter={this.props.center}
      defaultZoom={this.props.zoom}
    >
      <AnyReactComponent
        lat={59.955413}
        lng={30.337844}
        text="My Marker"
      />
    </GoogleMapReact>
  </div>
  );
  }
}
}
export default SimpleMap;
