import React, { useState } from 'react';
import apiDistance from './services/apiDistance';
import { FaMapMarkerAlt } from 'react-icons/fa';

import './App.css';
import './main.css';
import './global.css';
import './sidebar.css';

import DistanceForm from './components/DistanceForm/index';
import DistanceResult from './components/DistanceResult/index';
import Marker from './components/DistanceResult/index';

function App() {
  const [distance, setDistance] = useState('')
  const [duration, setDuration] = useState('')

  async function handleCalculation(data) {
    const response = await apiDistance.post('/Distance', data)
    const {distance, duration} = await response.data

    setDistance(distance)
    setDuration(duration)
  }

  const props = {
    key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA',
    lat: -23.17,
    lng: -45.85,
    zoom: 15
  }

  return (
    <div id="app">
      <aside>
        <strong>
            <FaMapMarkerAlt className="icon" />
            <p>Distance calculation</p>
        </strong>
        <DistanceForm onSubmit={handleCalculation} />
      </aside>
      <main>
        <DistanceResult distance={distance} duration={duration} />
        <Marker props={props}/>
      </main>

    </div>
    );
}

export default App;
