let getLatLng = {latitude: 0, longitude: 0}

function Maps() {

  navigator.geolocation.getCurrentPosition(
    (position) => {
      const { latitude, longitude } = position.coords
      
      console.log(lat, lng)
      return getLatLng;
    },
    (err) => {
      console.log(err)
    },
    {
      timeout: 30000,
    }
  )

const text = 'Maps'
const AnyReactComponent = ({ text }) => <div>{text}</div>;

const center = {
      lat: 59.95,
      lng: 30.33
}
const zoom = 11


  return(
    <div style={{ height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: 'AIzaSyB4ydoMyIkLCRLypbv9cbQ5ntIJEKDp9mA' }}
      defaultCenter={center}
      defaultZoom={zoom}
    >
      <AnyReactComponent
        lat={59.955413}
        lng={30.337844}
        text="My Marker"
      />
    </GoogleMapReact>
  </div>
  );
  }

export default Maps;
