import React, { useState } from 'react';
import apiDistance from './services/apiDistance';
import { FaMapMarkerAlt } from 'react-icons/fa';

import './App.css';
import './main.css';
import './global.css';
import './sidebar.css';

import DistanceForm from './components/DistanceForm/index';
import {Map, DistanceResult}  from './components/DistanceResult/index';

function App() {
  const [distance, setDistance] = useState('')
  const [duration, setDuration] = useState('')
  const [origins, setOrigins] = useState('')
  const [destinations, setDestinations] = useState('')

  async function handleCalculation(data) {
    const response = await apiDistance.post('/Distance', data)
    const {distance, duration, origins, destinations } = await response.data

    setDistance(distance)
    setDuration(duration)
    setOrigins(origins)
    setDestinations(destinations)
  }

  return (
    <div id="app">
      <aside>
        <strong>
            <FaMapMarkerAlt className="icon" />
            <p>Distance calculation</p>
        </strong>
        <DistanceForm onSubmit={handleCalculation} />
      </aside>
      <main>
      <DistanceResult distance={distance} duration={duration} />
        <Map distance={distance} origins={origins} destinations={destinations} />
      </main>

    </div>
    );
}

export default App;
