import React, { useState } from 'react';
import { FaWalking, FaCar, FaBicycle } from "react-icons/fa";

function DistanceForm( { onSubmit } ) {
    const [origins, setOrigins] = useState('')
    const [destinations, setDestinations] = useState('')
    const [mode, setMode] = useState('')
    const [isActive, setActive] = useState(false)

    async function handleSubmit(e) {
        e.preventDefault();

        await onSubmit({
            origins,
            destinations,
            mode,
        })

        setOrigins('')
        setDestinations('')
        setMode('')
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="input-block">
            <label htmlFor="origins">Origin</label>
            <input
                name="origins"
                id="origins"
                required
                value={origins}
                onChange={e => setOrigins(e.target.value)}
            />
            </div>

            <div className="input-block">
            <label htmlFor="destinations">Destiny</label>
            <input
                name="destinations"
                id="destinations"
                required
                value={destinations}
                onChange={e => setDestinations(e.target.value)}
            />
            </div>

            <div className="input-block">
             <ul>
                 <button className="btn-icon:" name="walking" id="walking" value={mode} onClick={e => setMode(e.target.value)}><FaWalking/></button>
                 <button name="driving" id="driving" value={mode} onClick={() => setActive(true)} style={{class: isActive ? "btn-icon" : "active"}}><FaCar/></button>
                 <button className="btn-icon"><FaBicycle/></button>
             </ul>
            </div>

            <div className="input-block">
            <label htmlFor="mode">Mode</label>
            <input
                name="mode"
                id="mode"
                //required
                value={mode}
                onChange={e => setMode(e.target.value)}
            />
            </div>

            <button type="submit">Go</button>
        </form>
    )
}

export default DistanceForm;
